import React, { useState, useCallback, useEffect } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';

interface MapPickerProps {
    initialLat?: number;
    initialLng?: number;
    onLocationSelect: (lat: number, lng: number) => void;
}

const containerStyle = {
    width: '100%',
    height: '100%',
    borderRadius: '1rem'
};

const defaultCenter = {
    lat: 12.9716,
    lng: 77.5946
};

// **IMPORTANT**: Replace with your valid Google Maps API Key
// You can also set this in a .env file as VITE_GOOGLE_MAPS_API_KEY
const GOOGLE_MAPS_API_KEY = import.meta.env.VITE_GOOGLE_MAPS_API_KEY || '';

const GoogleMapPicker: React.FC<MapPickerProps> = ({ initialLat, initialLng, onLocationSelect }) => {
    const { isLoaded, loadError } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: GOOGLE_MAPS_API_KEY
    });

    const [markerPos, setMarkerPos] = useState({ lat: initialLat || defaultCenter.lat, lng: initialLng || defaultCenter.lng });

    useEffect(() => {
        if (initialLat && initialLng) {
            // Only update if significantly different to avoid loops if float precision matches
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setMarkerPos(prev => {
                if (prev.lat === initialLat && prev.lng === initialLng) return prev;
                return { lat: initialLat, lng: initialLng };
            });
        }
    }, [initialLat, initialLng]);

    const onLoad = useCallback(function callback(map: google.maps.Map) {
        // map usage if needed, or leave empty but keep signature
        // rendering marker dynamically does not strictly require map ref here if using Marker component
        // To silence unused variable warning:
        console.log("Map Loaded", map.getZoom());
    }, []);

    const onUnmount = useCallback(function callback(map: google.maps.Map) {
        // Cleanup
        console.log("Map Unmounted", map.getZoom());
    }, []);

    const handleMapClick = (e: google.maps.MapMouseEvent) => {
        if (e.latLng) {
            const lat = e.latLng.lat();
            const lng = e.latLng.lng();
            setMarkerPos({ lat, lng });
            onLocationSelect(lat, lng);
        }
    };

    if (loadError) {
        return (
            <div className="h-full w-full flex items-center justify-center bg-gray-100 text-red-500 p-4 text-center">
                Error loading Google Maps. Please check your API Key.
            </div>
        );
    }

    if (!isLoaded) {
        return <div className="h-full w-full flex items-center justify-center bg-gray-100">Loading Map...</div>;
    }

    return (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={markerPos}
            zoom={15}
            onLoad={onLoad}
            onUnmount={onUnmount}
            onClick={handleMapClick}
            options={{
                streetViewControl: false,
                mapTypeControl: false,
                fullscreenControl: false,
            }}
        >
            <Marker position={markerPos} />
        </GoogleMap>
    );
};

export default GoogleMapPicker;

import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet icon issue
delete (L.Icon.Default.prototype as unknown as { _getIconUrl?: unknown })._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
    iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

interface MapPickerProps {
    initialLat?: number;
    initialLng?: number;
    onLocationSelect: (lat: number, lng: number) => void;
}

const LocationMarker: React.FC<{ onSelect: (lat: number, lng: number) => void, position: { lat: number; lng: number } | null }> = ({ onSelect, position }) => {
    useMapEvents({
        click(e) {
            onSelect(e.latlng.lat, e.latlng.lng);
        },
    });

    return position ? <Marker position={position} /> : null;
};

const Recenter: React.FC<{ lat: number; lng: number }> = ({ lat, lng }) => {
    const map = useMap();
    useEffect(() => {
        map.setView([lat, lng]);
    }, [lat, lng, map]);
    return null;
};

const MapPicker: React.FC<MapPickerProps> = ({ initialLat = 12.9716, initialLng = 77.5946, onLocationSelect }) => {
    const [position, setPosition] = useState<{ lat: number; lng: number } | null>(null);

    // If initial props change (e.g. from geolocation), update internal state
    useEffect(() => {
        if (initialLat && initialLng) {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setPosition(prev => {
                if (prev && prev.lat === initialLat && prev.lng === initialLng) return prev;
                return { lat: initialLat, lng: initialLng };
            });
        }
    }, [initialLat, initialLng]);

    const handleSelect = (lat: number, lng: number) => {
        setPosition({ lat, lng });
        // Reverse Geocoding would happen here in a real app to get address
        onLocationSelect(lat, lng);
    };

    return (
        <div className="h-full w-full relative z-0">
            <MapContainer
                center={[initialLat, initialLng]}
                zoom={13}
                className="h-full w-full rounded-xl"
                style={{ height: '100%', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                <LocationMarker onSelect={handleSelect} position={position} />
                {position && <Recenter lat={position.lat} lng={position.lng} />}
            </MapContainer>
            <div className="absolute bottom-2 left-2 bg-white px-2 py-1 rounded shadow text-xs z-[1000] opacity-80">
                Tap map to pin location
            </div>
        </div>
    );
};

export default MapPicker;

