            // Check if we actually need to update to avoid infinite loops/linter warnings if it were strict checks
            // For now, simpler to just set it as we depend on [user]
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setFormData(prev => {
                if (prev.email === user.email) return prev; // Basic stable check
                return {
                    name: user.name,
                    phone: user.phone || '',
                    email: user.email
                };
            });
        }
    }, [user]);

    const handleSave = () => {
        if (!user) return;
        updateProfile({
            name: formData.name,
            phone: formData.phone
        });
        setIsEditing(false);
    };

    if (!user) return <div className="p-8 text-center">Please login to view profile.</div>;

    // Calc Stats
    const complaints = db.getComplaints().filter(c => c.userId === user.id);
    const resolved = complaints.filter(c => c.status === 'Resolved').length;

