import { Link } from 'react-router-dom';
import { AlertCircle, CheckCircle, Clock } from 'lucide-react';
import Card from '../../components/ui/Card';
import { db } from '../../services/storage';
import type { Complaint } from '../../services/storage';

const EmployeeDashboard: React.FC = () => {
    const [recentAssigned, setRecentAssigned] = useState<Complaint[]>([]);

    useEffect(() => {
        const complaints = db.getComplaints();
        setRecentAssigned(complaints.filter(c => c.status !== 'Resolved').slice(0, 5));
    }, []);

    // Derived stats from DB directly (or state if we were using it for complaints list)
    // Since we just fetch once, we can just calculate.
    // Ideally we should hold complaints in state if we want to be reactive, but for now we follow the pattern.
    const allComplaints = db.getComplaints();
    const now = Date.now();
    const stats = {
        assigned: allComplaints.filter(c => c.status === 'Assigned').length,
        pending: allComplaints.filter(c => c.status === 'In Progress' || c.status === 'Submitted').length,
        resolved: allComplaints.filter(c => c.status === 'Resolved').length,
