import React, { useState, useEffect } from 'react';
import { Briefcase, LogOut, TrendingUp, CheckCircle, Mail, Edit2 } from 'lucide-react';
import Card from '../../components/ui/Card';
import Input from '../../components/ui/Input';
import Button from '../../components/ui/Button';
import { useAuth } from '../../context/AuthContext';

const EmployeeProfile: React.FC = () => {
    const { user, logout, updateProfile } = useAuth();
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        department: 'Sanitation'
    });

    useEffect(() => {
        if (user) {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            setFormData(prev => {
                if (prev.name === user.name && prev.department === (user.department || 'Sanitation')) return prev;
                return {
                    name: user.name,
                    department: user.department || 'Sanitation'
                };
            });
        }
    }, [user]);

    const handleSave = () => {
        if (!user) return;
        updateProfile({
            name: formData.name,
            department: formData.department
        });
        setIsEditing(false);
    };

    if (!user) return <div className="p-8 text-center">Please login.</div>;

    // Calc Stats (Mock for Employee as we don't track per-employee resolution count in simple DB yet)
    // Actually we can count how many 'Resolved' in general if we assume one dept or assign logic
    // For now, let's just make it dynamic based on *something* or random to look alive
    const resolvedCount = 42;

    return (
        <div className="min-h-screen bg-gray-50 pb-24">
            {/* Header Banner */}
            <div className="bg-gradient-to-r from-emerald-600 to-teal-600 h-48 relative overflow-hidden">
                <div className="absolute inset-0 bg-[url('https://www.transparenttextures.com/patterns/cubes.png')] opacity-10"></div>
                <div className="absolute bottom-[-10%] right-[-5%] w-48 h-48 bg-white opacity-10 rounded-full blur-3xl"></div>
            </div>

            <div className="max-w-3xl mx-auto px-4 -mt-20">
                {/* Profile Card */}
                <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-100 flex flex-col items-center text-center relative overflow-hidden">
                    <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-emerald-400 to-teal-500"></div>
                    <div className="h-28 w-28 bg-white rounded-full p-2 shadow-lg mb-4 mt-2">
                        <div className="h-full w-full bg-emerald-100 rounded-full flex items-center justify-center text-emerald-600">
                            <span className="text-4xl font-bold">{user.name.charAt(0).toUpperCase()}</span>
                        </div>
                    </div>

                    {!isEditing ? (
                        <>
                            <h1 className="text-2xl font-bold text-gray-900">{user.name}</h1>
                            <p className="text-emerald-600 font-medium bg-emerald-50 px-3 py-1 rounded-full text-xs mt-2 uppercase tracking-wide">
                                {user.department || 'General Staff'}
                            </p>
                            <button
                                onClick={() => setIsEditing(true)}
                                className="absolute top-4 right-4 text-gray-400 hover:text-emerald-600 p-2"
                            >
                                <Edit2 className="h-4 w-4" />
                            </button>
                        </>
                    ) : (
                        <div className="w-full max-w-sm space-y-3 mt-2">
                            <Input
                                label="Name"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                            />
                            <Input
                                label="Department"
                                value={formData.department}
                                onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                            />
                            <div className="flex gap-2 justify-center mt-2">
                                <Button variant="outline" onClick={() => setIsEditing(false)} className="py-2 px-4 text-xs">Cancel</Button>
                                <Button onClick={handleSave} className="py-2 px-4 text-xs">Save</Button>
                            </div>
                        </div>
                    )}

                    <p className="text-gray-500 text-sm mt-4">ID: EMP-{user.id.slice(0, 4).toUpperCase()}</p>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                    <Card className="bg-gradient-to-br from-emerald-500 to-teal-600 text-white border-none shadow-lg">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-emerald-100 text-xs font-bold uppercase tracking-wide">Performance Score</p>
                                <p className="text-3xl font-bold mt-1">98/100</p>
                                <p className="text-emerald-100 text-xs mt-1">+2.4% from last month</p>
                            </div>
                            <div className="bg-white/20 p-3 rounded-xl backdrop-blur-sm">
                                <TrendingUp className="h-8 w-8 text-white" />
                            </div>
                        </div>
                    </Card>

                    <Card className="bg-white border-none shadow-md">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-gray-500 text-xs font-bold uppercase tracking-wide text-left">Tasks Resolved</p>
                                <p className="text-3xl font-bold mt-1 text-gray-900 text-left">{resolvedCount}</p>
                                <p className="text-green-600 text-xs mt-1 text-left font-medium">This Month</p>
                            </div>
                            <div className="bg-green-50 p-3 rounded-xl">
                                <CheckCircle className="h-8 w-8 text-green-600" />
                            </div>
                        </div>
                    </Card>
                </div>

                <div className="mt-6 space-y-6">
                    <Card>
                        <h3 className="font-bold text-gray-900 mb-4 flex items-center">
                            <Briefcase className="h-4 w-4 mr-2 text-gray-400" />
                            Employment Details
                        </h3>
                        <div className="space-y-4">
                            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                <span className="text-sm text-gray-500 flex items-center"><Mail className="h-3 w-3 mr-2" />Email</span>
                                <span className="text-sm font-medium text-gray-900">{user.email}</span>
                            </div>
                            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                <span className="text-sm text-gray-500">Zone Assignment</span>
                                <span className="text-sm font-medium text-gray-900">North Zone - Ward 4, 5, 6</span>
                            </div>
                        </div>
                    </Card>

                    <button
                        onClick={logout}
                        className="w-full flex items-center justify-center p-4 bg-white border-2 border-red-50 text-red-600 font-bold rounded-2xl hover:bg-red-50 hover:border-red-100 transition-all shadow-sm"
                    >
                        <LogOut className="h-5 w-5 mr-2" />
                        Log Out System
                    </button>

                    <div className="text-center text-xs text-gray-400 pb-8">
                        Municipal Employee Portal v2.1
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EmployeeProfile;
